With({fItems: Filter(col_p,Upper(menuKey)=Upper(pbMenu_1.MenuKey))}, 
    If(CountRows(fItems)=0, 
        [
            pbMenu_1.MissingMenuRecord
        ]
        , 
        ShowColumns(fItems,ItemKey,ItemDisplayName,ItemIconName, ItemAppearance, ItemIconStyle, ItemTooltip, ItemDisabled)
    )
)

With({newMenuItem: {menuKey: Upper(p_menuKey), ItemKey: Upper(p_ItemKey), ItemDisplayName: p_ItemDisplayName, ItemIconName: p_ItemIconName, ItemAppearance: p_ItemAppearance, ItemIconStyle: p_ItemIconStyle, ItemTooltip: p_ItemTooltip, ItemDisabled: false, disableOnSelect: p_disableOnSelect, confirmItemAction: p_confirmItemAction}}, 
    RemoveIf(col_pbMenu,menuKey=newMenuItem.menuKey && ItemKey=newMenuItem.ItemKey);
    Collect(col_pbMenu,newMenuItem);
    newMenuItem 
)

With({newMenuItem: 
        //get menu item structure
        {
            menuKey: Upper(TrimEnds(p_menuKey)), 
            ItemKey: Upper($"_info_{GUID()}"), 
            ItemDisplayName: TrimEnds(p_ItemDisplay), 
            ItemIconName: "Info", 
            ItemAppearance: TrimEnds(p_itemAppearance), 
            ItemIconStyle: pbMenu_1.ItemIconStyleChoices.Filled, 
            ItemTooltip: TrimEnds(p_ItemTooltip), ItemDisabled: isSpacer, disableOnSelect: p_disableOnSelect, confirmItemAction: p_confirmItemAction}
    )}, 
    If(p_addToCollection=true, 
        RemoveIf(col_pbMenu,menuKey=newMenuItem.menuKey && ItemKey=newMenuItem.ItemKey);
        Collect(col_pbMenu,newMenuItem)
    );
    newMenuItem 
)

With({Perf: {id: GUID(), start: Now(), action: $"ITEM STATE CHANGE: {pbMenu_1.MenuKey}.{p_itemKey} (enabled: {p_enabled})"}}, 
    RemoveIf(col_pbMenuUtil,menuGuid=GUID(pbMenu_1.ControlGUID) && action="disable" && Upper(itemKey)=Upper(p_itemKey));
    With({isSpacer:StartsWith(Lower(p_itemKey),"_spacer_")}, 
        With({tmpMenuItem: pbMenu_1.MenuItem(pbMenu_1.MenuKey,p_itemKey), shouldDisable: If(isSpacer, true, Not(p_enabled))}, 
            If(p_enabled=false, 
                Collect(col_pbMenuUtil,{menuGuid: GUID(pbMenu_1.ControlGUID), menuKey: pbMenu_1.MenuKey,itemKey: p_itemKey, action: "disable", boolValue: true,  desc: ""});
            );
            If(tmpMenuItem.ItemDisabled<>shouldDisable, 
                Patch(col_pbMenu,tmpMenuItem,{ItemDisabled:shouldDisable})
            )
        )
    );
    If(pbMenu_1.PerformanceLogging=true, 
        With({endTS: Now()}, 
            Collect(col_pbMenuPerf,{id: Perf.id, elapsed: DateDiff(Perf.start,endTS,TimeUnit.Milliseconds), start: Perf.start, end: Now(),  action: Perf.action})
        )
    )
)
